  $("#searchFilter").on("keyup", function() {
    var value = $(this).val().toLowerCase();
    document.querySelectorAll('.myTable .tableRespData').forEach(function(tr){
      var chk = false;
      tr.querySelectorAll('td').forEach(function(td, i){
        if(i < 4){
          if(td.innerText.search(value) > -1){
            chk = true;
          }
        }
      });
      if(chk == true){
        tr.style.display = 'table-row';
      }else{
        tr.style.display = 'none';
      }
    });
    // $(".myTable .tableRespData").filter(function() {
    //   $(this).toggle( $(this).text().toLowerCase().indexOf(value) > -1)
    // });
  });

  function printProfile(clicked_id){

    event.preventDefault();
   var id=clicked_id;

   var profimg=$('.profimage'+id).text();
   // $('.mdtl_img').attr('src','');
    var prof_gender=$('.profgender'+id).text();
     $('.mdtl_gender').text(prof_gender);

     if(profimg=="" && prof_gender=='male'){
          $('.mdtl_img').attr('src', "../../imgs/male_profile1.png"+profimg);
      }
      else if(profimg=="" && prof_gender=='female'){
       
         $('.mdtl_img').attr('src', "../../imgs/girl_profile.png"+profimg);
      }
      else{
         $('.mdtl_img').attr('src', "../../imgs/"+profimg);
      }
    var profName=$('.profnameNxt'+id).text();
    $('.mdtl_name').text(profName);
    var profmatrid=$('.profmatrid'+id).text();
    $('.mdtl_matrid').text(profmatrid);
    var pro_maritstatus=$('.profmatrtalstatus'+id).text();
    $('.mdtl_maritalstatus').text(pro_maritstatus);
    var pro_religion=$('.profreligion'+id).text();
    $('.mdtl_religion').text(pro_religion); 
    var pro_edu=$('.profeducation'+id).text();
    $('.mdtl_edu').text(pro_edu);
     var pro_job=$('.profemployee'+id).text();
    $('.mdtl_job').text(pro_job);
    var pro_dob=$('.pro_dob'+id).text();
    $('.mdtl_dob').text(pro_dob);
    var pro_income=$('.profincome'+id).text();
   $('.mdtl_income').text(pro_income);
   var pro_district=$('.profdistrict'+id).text();
 $('.mdtl_district').text(pro_district); 
 var pro_subcaste=$('.profsubcaste'+id).text();
 $('.mdtl_subcaste').text(pro_subcaste);
 var pro_caste=$('.profcaste'+id).text();
 $('.mdtl_caste').text(pro_caste);
 var pro_age=$('.profage'+id).text();
 $('.mdtl_age').text(pro_age);
 var pro_gold=$('.profgold'+id).text();
 var pro_amount=$('.profamount'+id).text();
 var pro_acres=$('.profacres'+id).text();
 $('.mdtl_gift').text(pro_amount+","+pro_gold+","+pro_acres);
  }

 function DirectLogin(e){
     var user= e.id;
      $.ajax({
         type: "post",
          url: "sup_ad_direct_login",
         headers: {'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')},
         data: {
              "user":user,"role":"SupAd"
            },
         cache: false,
         success: function(data){
            if(data.role==1){
            // window.location.href="../admin/login/home";
            window.open('../../admin/login/home','_blank');
           }
           else if(data.role==2){
            // window.location.href="../shopUsers/profile_management";
            window.open('../../shopUsers/profile_management','_blank');
           }
           else if(data.role==0){
            // window.location.href="../register2";
           window.open('../../register2','_blank');
           }
           else if(data.role==3){
            // window.location.href="../shopManager/manager_management";
            window.open('../../shopManager/manager_management','_blank');
           } else if(data.role==5){
            // window.location.href="superAdmin/home";
             window.open('../superAdmin/home','_blank');
           }
           else{
             alert("failiure")
           }
          }
       });
    }

  </script>

